import React from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const navigate = useNavigate();

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Farmer Dashboard</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem' }}>
        <button onClick={() => navigate('/schemes')}>Government Schemes</button>
        <button onClick={() => navigate('/waste-management')}>Waste Management</button>
        <button onClick={() => navigate('/soil-water-management')}>Soil & Water Management</button>
        <button onClick={() => navigate('/feedback')}>Share Your Feedback</button>
        <button onClick={() => navigate('/contact')}>Contact Us</button>
        <button onClick={() => navigate('/about')}>About</button>
      </div>
    </div>
  );
}

export default Dashboard;
